mapper.select:
SELECT s.sn stockSn, s.id stockId, s.finess, s.attachment, lswo.origin_serial_no, lswo.express_number, DATE_FORMAT(lswo.task_arrive_time,'%Y-%m-%d %H:%i:%s') taskArriveTime, DATE_FORMAT(lswo.created_time,'%Y-%m-%d %H:%i:%s') createdTime, lswo.work_type, lswo.work_source, lswo.created_by, lswo.opt_type, lswo.exception_mark, s.goods_id
FROM log_store_work_opt lswo LEFT JOIN stock s ON lswo.stock_id = s.id
lswo.belonging_store_id = #{request.belongingStoreId}
lswo.belonging_store_id in
#{id}
and DATE_FORMAT(lswo.created_time,'%Y-%m-%d %H:%i:%s') between #{request.startTime} and #{request.endTime}
and s.sn like concat('%',#{request.stockSn,jdbcType=VARCHAR},'%')
and lswo.express_number like concat('%',#{request.expressNumber,jdbcType=VARCHAR},'%')
and lswo.origin_serial_no like concat('%',#{request.originSerialNo,jdbcType=VARCHAR},'%')
and lswo.work_source = #{request.workSource}
and lswo.created_by like concat('%',#{request.createdBy,jdbcType=VARCHAR},'%')
and lswo.opt_type = #{request.optType}
and lswo.opt_type IN (10,20,30)
and lswo.opt_type IN (40,50)
order by lswo.id desc
